    public static int removeDuplicatesTLE(ArrayList<Integer> a) {
        int n = a.size(), i=n-2, j = n-1;
        if(n==1) return 1;
        while(i>=0){
            if(a.get(i).equals(a.get(i+1))){
                a.add(a.remove(i+1));
            }
            i--;
        }

//        System.out.println(a);

        for(i=0;i<a.size()-1;i++){
            if(a.get(i)>=a.get(i+1)) return i+1;
        }

        return a.size();
    }
